rl -s https://raw.githubusercontent.com/certbot/certbot/master/certbot-nginx/certbot_nginx/_internal/tls_configs/options-ssl-nginx.conf > \"$data_path/conf/options-ssl-nginx.conf\"","  curl -s https://raw.githubusercontent.com/certbot/certbot/master/certbot/certbot/ssl-dhparams.pem > \"$data_path/conf/ssl-dhparams.pem\"","  echo","fi","","echo \"### Creating dummy certificate for $domains ...\"","path=\"/etc/letsencrypt/live/$domains\"","mkdir -p \"$data_path/conf/live/$domains\"","docker-compose run --rm --entrypoint \"\\","  openssl req -x509 -nodes -newkey rsa:4096 $rsa_key_size -days 1\\","    -keyout '$path/privkey.pem' \\","    -out '$path/fullchain.pem' \\","    -subj '/CN=localhost'\" certbot","echo","","","echo \"### Starting nginx ...\"","docker-compose up --force-recreate -d nginx","echo","","echo \"### Deleting dummy certificate for $domains ...\"","docker-compose run --rm --entrypoint \"\\","  rm -Rf /etc/letsencrypt/live/$domains && \\","  rm -Rf /etc/letsencrypt/archive/$domains && \\","  rm -Rf /etc/letsencrypt/renewal/$domains.conf\" certbot","echo","","","echo \"### Requesting Let's Encrypt certificate for $domains ...\"","#Join $domains to -d args","domain_args=\"\"","for domain in \"${domains[@]}\"; do","  domain_args=\"$domain_args -d $domain\"","done","","# Select appropriate email arg","case \"$email\" in","  \"\") email_arg=\"--register-unsafely-without-email\" ;;","  *) email_arg=\"--email $email\" ;;","esac","","# Enable staging mode if needed","if [ $staging != \"0\" ]; then staging_arg=\"--staging\"; fi","","docker-compose run --rm --entrypoint \"\\","  certbot certonly --webroot -w /var/www/certbot \\","    $staging_arg \\","    $email_arg \\","    $domain_args \\","    --rsa-key-size $rsa_key_size \\","    --agree-tos \\","    --force-renewal\" certbot","echo","","echo \"### Reloading nginx ...\"","docker-compose exec nginx nginx -s reload"],"stylingDirectives":[[{"start":0,"end":11,"cssClass":"pl-c"},{"start":0,"end":2,"cssClass":"pl-c